import React, { useEffect } from 'react'
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import { useState } from 'react';
import { Link } from 'react-router-dom';

function Card( van, classHolder) {
    console.log(van)
    const [img, setImg]= useState("");
    const [name, setName]= useState("");
    const [price, setPrice] =  useState("");
    useEffect(()=>{
       setImg(van.imageUrl);
       console.log(img);
       setName(van.setName);
       setPrice(van.price);
    },[van])
    return (
        <div className='van-card'>
                     { img &&  <img src={img} alt="nothing-here"/>}  
                     {!img &&<Skeleton width="300px" height="100px"/> }
                      <div className='van-id'>
                        <div className='van-but'>
                        <h2>{name || <Skeleton count={1} width="100%" />}</h2>
                        <p>{"$"+price || <Skeleton count={1} width="100%" />}<p>/day</p></p>
                        </div>
                        <br/>
                       <p className={classHolder} > <Link>{van.type}</Link></p>
                      </div>

                      </div>
    )
}

export default Card
