/* eslint-disable react-hooks/exhaustive-deps */
import React from 'react'
import Footer from '../components/Footer'
import Nav from '../components/Nav'
import { useEffect, useState } from 'react'
import {Link} from "react-router-dom"
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'

function Vans() {
    const [vanData, setVanData]= useState(null);
    const [title, setTitle] = useState(null);



    useEffect(
      async ()=>{
        const res = await fetch( "https://json-mock-czwb.onrender.com/vans");
        const data = await res.json();
        setVanData(data)
      }
      ,[])
    var classHolder;
    return (
        <div>
            <Nav/>
            <section className='van-container'>
            <h2 className='van-page-title'>Explore Our Van Options</h2>
            <div className='filter-container'>
             <Link to="/vans/filter/simple"> Simple</Link>
             <Link  to="/vans/filter/rugged"> Rugged</Link>
             <Link  to="/vans/filter/luxury">Luxury</Link>
             <Link to="/vans">Clear Filter</Link>
            </div>
                <div className='van-holder'>

              {vanData && 
            vanData.map((van)=>{

              if (van.type==="simple"){
                    classHolder = "orange"
              }else if(van.type==="rugged"){
                  classHolder= "green"
              }else{
                  classHolder="black";
              }
               return <div className='van-card'>
                      <img src={van.imageUrl} alt="nothing-here"/>
                      <Skeleton width="300px" height="350px"/>
                      <div className='van-id'>
                        <div className='van-but'>
                        <h2>{title || <Skeleton count={1} width="100%" />}{van.name}</h2>
                        <p>{title || <Skeleton count={1} width="100%" />}${van.price}<p>/day</p></p>
                        </div>
                        <br/>
                       <p className={classHolder} > <Link>{van.type}</Link></p>
                      </div>

                      </div>

            })
              }
              {
                !vanData && <Skeleton/>
              }
              </div>
            </section>
            <Footer/>
        </div>
    )
}

export default Vans
