import React from 'react'
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import { useState } from 'react';
import { Link } from 'react-router-dom';

function Card( van, classHolder) {
    const [img, setImg]= useState("");
    const [name, setName]= useState(null);
    const [price, setPrice] =  useState("");
    return (
        <div className='van-card'>
                     { img &&  <img src={van.imageUrl} alt="nothing-here"/>}  

                      <div className='van-id'>
                        <div className='van-but'>
                        <h2>{name || <Skeleton count={1} width="100%" />}</h2>
                        <p>{"$"+price || <Skeleton count={1} width="100%" />}<p>/day</p></p>
                        </div>
                        <br/>
                       <p className={classHolder} > <Link>{van.type}</Link></p>
                      </div>

                      </div>
    )
}

export default Card
