import React from 'react'
import { useParams } from 'react-router-dom'
import {Link} from "react-router-dom"
import { useEffect, useState} from 'react';
import Nav from '../components/Nav';
import Footer from '../components/Footer';

function Vanfilter() {
    const param = useParams();
    const [vanData, setVanData]= useState([]);
    useEffect(()=>{
       fetch( "http://localhost:3000/vans")
       .then(res => res.json())
       .then(data => setVanData(data))
       console.log(vanData);
    },[])
    var classHolder;
    return (
        <div>
            <Nav/>
            <section className='van-container'>
            <h2 className='van-page-title'>Explore Our Van Options</h2>
            <div className='filter-container'>
             <Link to="/vans/filter/simple"> Simple</Link>
             <Link> Rugged</Link>
             <Link>Luxury</Link>
             <Link to="/vans">Clear Filter</Link>
            </div>
                <div className='van-holder'>

              {
            vanData.filter((van)=>{

              if (van.type==="simple"){
                    classHolder = "orange"
              }else if(van.type==="rugged"){
                  classHolder= "green"
              }else{
                  classHolder="black";
              }
              if (van.type===param.type){
               console.log(van)
              }
            })
              }
              </div>
            </section>
            <Footer/>
        </div>
    )
}

export default Vanfilter
